import React, {Component} from 'react'

function FachListe (props) {
  return (
    <div>
      <ul>
      {props.faecher.map((fach) => 
        <li key = {fach}>
          <div>
            <span>{fach}</span>
          </div>
        </li>        
      )}
      </ul>
    </div>
  )
}

class Faecher extends Component {
  constructor(props) {
    super(props);

    this.state = {
      faecher: [],
      input: '',
    }
  }

  updateInput (e) {
    const value = e.target.value

    this.setState(() => {
      return {input: value}
    })
  }

  handleFachHinzufuegen() {
    this.setState((currentState) => {
      return {
        faecher: currentState.facher.concat(currentState.input),
        input: ''
      }
    })
  }

  

  render() {
    return (
      <div>
        <p>Faecher wird gerendert</p>
        <input 
          type = 'text'
          placeholder = 'fach'
          value = {this.state.input}
          onChange={this.updateInput}
        />
        <button onclick={() => this.handleFachHinzufuegen}> + </button>
        <FachListe 
          faecher = {this.state.faecher}
        />
      </div>
    )
  }
}

export default Faecher;
